custom.scss:
@font-face {
    font-family:'FuturaEFOP-Book';
    src: url('../fonts/FuturaEFOP-Book.eot'); 
    src: url('../fonts/FuturaEFOP-Book.eot?#iefix') format('embedded-opentype'),
        url('../fonts/FuturaEFOP-Book.woff2') format('woff2'),
        url('../fonts/FuturaEFOP-Book.woff') format('woff'),
        url('../fonts/FuturaEFOP-Book.ttf') format('truetype'),
        url('../fonts/FuturaEFOP-Book.svg#FuturaEFOP-Book') format('svg');
    font-weight: normal;
    font-style: normal;
}

@font-face {
    font-family:'FuturaEFOP';
    src: url('../fonts/FuturaEFOP-DemiBold.eot');
    src: url('../fonts/FuturaEFOP-DemiBold.eot?#iefix') format('embedded-opentype'),
        url('../fonts/FuturaEFOP-DemiBold.woff2') format('woff2'),
        url('../fonts/FuturaEFOP-DemiBold.woff') format('woff'),
        url('../fonts/FuturaEFOP-DemiBold.ttf') format('truetype'),
        url('../fonts/FuturaEFOP-DemiBold.svg#FuturaEFOP-DemiBold') format('svg');
    font-weight:600;
    font-style:normal;
}

body {font-family:'FuturaEFOP-Book'; color:#000; font-size:16px;padding-top: 106px;}
.body {margin-top:0;margin-bottom: 30px;}
.clearfix{
    clear: both;
    &:before {
        display: block;
        content: " ";
    }
    &:after {
        display: block;
        content: "";
        clear: both;
    }
}
@mixin clearfix(){
    clear: both;
    &:before {
        display: block;
        content: " ";
    }
    &:after {
        display: block;
        content: "";
        clear: both;
    }
}
@mixin displayflexaligncenter(){
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-align: center;
    -webkit-align-items: center;
    -webkit-box-align: center;
    align-items: center;
}
@mixin displayflexjustifycenter(){
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-align: center;
    -webkit-align-items: center;
    -webkit-box-align: center;
    align-items: center;
    justify-content: center;
}
.container {
    padding: 0 15px;
}
h1, h2, h3, h4, h5, h6
{	font-family:'FuturaEFOP-Book'; color:#000;
}
a
{	text-decoration:none; outline:none; color:#36BAE0;
}
a:hover 
{	color:#36BAE0;
}
.heroCarousel-slide
{   .heroCarousel-image, .heroCarousel-image-wrapper
    {   max-height:100%;
    }
}
.heroCarousel
{   &.slick-initialized
    {   max-height:100%;
    } 
    margin:0 auto;
}
.slick-dotted.slick-slider
{   margin-bottom:0;
}
.navUser-action 
{   &:hover, &:focus
    {   color:#999;
    }
}
.navUser_right .navUser-action:hover  {
  color:#ccc;
}
.button
{   background:#00132c; font-size:14px; color:#fff; line-height:normal; border:0; border-radius:0;
    outline:none; letter-spacing:normal; text-transform:uppercase; -webkit-transition:all .3s linear;
    -moz-transition:all .3s linear; transition:all .3s linear; font-family:'FuturaEFOP';
    &:focus, &:active
    {   background:#00132c; font-size:14px; color:#fff; line-height:normal; border:0; border-radius:0;
        outline:none; letter-spacing:normal; text-transform:uppercase; -webkit-transition:all .3s linear;
        -moz-transition:all .3s linear; transition:all .3s linear; font-family:'FuturaEFOP';
  }
}
.swal2-container
{   button.swal2-confirm.button, .swal2-modal .swal2-cancel
    {   background:#00132c; font-size:14px; color:#fff; line-height:normal; border:0; border-radius:0;
        outline:none; letter-spacing:normal; text-transform:uppercase; -webkit-transition:all .3s linear;
        -moz-transition:all .3s linear; transition:all .3s linear; font-family:'FuturaEFOP';
    }
}
.modal--alert .button-container .confirm
{   background:#00132c; font-size:14px; color:#fff; line-height:normal; border:0; border-radius:0;
    outline:none; letter-spacing:normal; text-transform:uppercase; -webkit-transition:all .3s linear;
    -moz-transition:all .3s linear; transition:all .3s linear; font-family:'FuturaEFOP';
    &:active
    {   background:#00132c; font-size:14px; color:#fff; line-height:normal; border:0; border-radius:0;
        outline:none; letter-spacing:normal; text-transform:uppercase; -webkit-transition:all .3s linear;
        -moz-transition:all .3s linear; transition:all .3s linear; font-family:'FuturaEFOP';
    }
}
.button:hover
{   background:#072750; font-size:14px; color:#fff; line-height:normal; border:0; border-radius:0;
    outline:none; letter-spacing:normal; text-transform:uppercase; -webkit-transition:all .3s linear;
    -moz-transition:all .3s linear; transition:all .3s linear; font-family:'FuturaEFOP';
}
.swal2-container
{   button.swal2-confirm.button:hover, .swal2-modal .swal2-cancel:hover
    {   background:#072750; font-size:14px; color:#fff; line-height:normal; border:0; border-radius:0;
        outline:none; letter-spacing:normal; text-transform:uppercase; -webkit-transition:all .3s linear;
        -moz-transition:all .3s linear; transition:all .3s linear; font-family:'FuturaEFOP';
    }
}
.modal--alert .button-container .confirm:hover
{   background:#072750; font-size:14px; color:#fff; line-height:normal; border:0; border-radius:0;
    outline:none; letter-spacing:normal; text-transform:uppercase; -webkit-transition:all .3s linear;
    -moz-transition:all .3s linear; transition:all .3s linear; font-family:'FuturaEFOP';
}
