.form-wishlist{
    .dropdown-menu-button{
        @include displayflexjustifycenter;
    }
    #wishlist-dropdown{
        &.is-open{
            padding: 0;
            margin: 5px 0 0;
            .button{
                background: #00132C;
                &:hover{
