#previewModal {
    .productView-image {margin:15px 0 15px 0;
        @include breakpoint("medium"){margin-top: 0;}
        img {margin:0 auto; text-align:center; display:block; width:auto; position:absolute;}
    }
    .modal-body{
        padding: 15px;
        @include breakpoint("xxsmall"){padding: 2.25rem;}
    }
}

.previewCart{
    .productView {
        @include breakpoint(800px){
            width: 65%;
            margin: 0 auto;
            @include breakpoint("medium"){
                margin: 0;
                padding: 0 .75rem 0 0;
                float: left;
                display: -webkit-box;
                display: -ms-flexbox;
                display: flex;
                width: 75%;
            }
        }
        .productView-image{
            width: 100%;
            float: none;
            @include breakpoint(800px){
                padding-right: 0;
                @include breakpoint("medium"){
                    padding-right: .75rem;
                    padding-left: 0;
                    width: 58.33333%;
                    float: left;
                    display: -webkit-box;
                    display: -ms-flexbox;
                    display: flex;
                    -webkit-box-orient: vertical;
                    -webkit-box-direction: normal;
                    -ms-flex-direction: column;
                    flex-direction: column;
                }
            }
        }
        .productView-details {
            text-align: center;
            @include breakpoint(800px){
                width: 100%;
                padding: 0;
            }
            @include breakpoint("medium"){
                text-align: left;
                padding-left: .75rem;
                padding-right: .75rem;
                width: 41.66667%;
                float: left;
                width: 41.66667%;
            }
        }
        .productView-info{
            text-align: center;
            @include breakpoint("small"){
                margin: 0;
            }
            @include breakpoint("medium"){
                text-align: left;
            }
            .productView-info-name,
            .productView-info-value{
                float: none;
                display: inline-block;
                @include breakpoint("medium"){
                    display: block;
                }
            }
        }
    }
    .previewCartCheckout{
        .button{
            padding-left: 5px;
            padding-right: 5px;
        }
    }
}
.card{
    margin-bottom: 16px;
}
.wishlist_details{
    .card {margin:0; padding:0;}
    .productGrid .product > .form {margin:0; padding:0;
        input.button {font-size:16px; display:block; width:100%; margin:5px 0 10px; padding:10px 5px; height:auto;letter-spacing:1px;}
    }
}
