    button {border-color:#ccc; background:#ccc;}
    &.slick-active button{border-color:#29166f;}
    button:before {color:#29166f;}
    &.slick-active button:before{color:#29166f; background:#29166f;}
    button {height:10px;width:10px;padding:0; height:14px; width:14px;
        &:hover:before{background:#29166f;}
        &:before{height:10px; width:10px;}
    }
}

