#gift-certificate-form > .form-row.form-row--half + .form-row .form-fieldset.form-column > .form-field
{   padding-left:0; padding-right:0;
    &.form-field-group
    {   padding-left:0.75rem; padding-right:0.75rem;
    }
}
.error_page_404 {
    display:inline-block; text-align:center; width:100%; color:#000; letter-spacing:1px; margin-top:20px;
    h1 {
        display:inline-block; text-align:center; width:100%; color:#000; letter-spacing:1px; margin-top:20px;font-size: 22px;
        @include breakpoint("xxsmall"){font-size: 36px;}
    }
    p {
        font-size:16px; letter-spacing:0.5px; color:#000; font-weight:500; margin:0;
    }
    img {
        max-width: 300px;
    }
}

.error_page_403
{   .form #search_query_adv
    {   display:block; width:calc(100% - 110px); margin:0 0 10px;
    }
    p
    {   font-size:16px; letter-spacing:0.5px; color:#000; font-weight:500;
    }
    .form-prefixPostfix
    {   position:relative; display:block; max-width:75%; margin:0 auto;
    }   
    .form .button.button--primary
    {   margin:0; position:absolute; top:0; right:0; height:100%; width:120px; padding:0; display:block;
        line-height:normal; border-radius:0;
    }       
}
.modal-background{background:rgba(0,0,0,0.50);}
table.tblFontSmall
{   margin:0 0 16px;
}
table.tblFontSmall tr:nth-child(odd) td
{   background:#f2f2f2;
}
table.tblFontSmall tr td p
{   margin:0; padding:6px;
}
.header {border:0; box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.1);position: absolute;
    @include breakpoint("medium"){
        position: fixed;
        box-shadow:0 1px 1px 0 rgba(0, 0, 0, .1);
    }
    &.is-open {
        height: auto;
    }
}
.header_top {width:100%;
    @include breakpoint("medium"){
        margin-top:25px;
    }
    .navUser-action--quickSearch{
        display: none;
        @include breakpoint("medium"){display: block;}
    }
}
.header-logo {margin:0;
    @include breakpoint("medium"){
        text-align:left; float:left; width:27%;
    }
    svg{
        width: 100%;
        height: auto;
        max-height: 28px;
        @include breakpoint("medium"){
            width: auto;
        }
    }
    > a{
        width: auto;
        max-width: 200px;
        padding: 1.4rem 0;
        @include breakpoint("medium"){
            max-width: 265px;
            padding: 0.78571rem 0;
        }
    }
}
.header-logo-image-container:after 
{	padding:0;
}
.navUser {padding:2px 12px 0 0; width:73%; float:right;
    @include breakpoint("medium"){padding:4px 0 0;}
}
.navUser-action 
{	padding:0; line-height:24px; vertical-align:middle; color:#000; font-size:16px; font-weight:400;
	text-transform:capitalize;
}
.navUser-action svg 
{	width:24px; height:24px; vertical-align:middle; margin-top:-2px;
}
.navUser-item--faq svg, .navUser-item--account svg
{	margin-right:5px;
}
.navUser-item--cart .navUser-action {color:#000; margin-left:22px;position: relative;}
.navUser-item--account
{	margin-top:-2px;
}
.navUser-item--account .navUser-action
{	margin-left:22px; margin-top:-1px;
}
.header_bottom {
    width: 100%;
    @include breakpoint("medium"){margin-top:21px;position: relative;}
    .navUser-item-search{
        button{
            display: none;
        }
        .container{
            padding: 0;
            @include breakpoint("xxsmall"){padding-right: 15px; padding-left: 15px;}
        }
        @media (max-width:990px){
            .search_close{
                display: none;
            }
            .navUser-action--quickSearch{
                display: none;
            }
            .dropdown--quickSearch{
                left: 32px;
                right: 0;
                left: 0;
                top: 0;
                position: relative;
                .form {
                    max-width: 290px;
                    margin: 0 auto 10px;
                    @include breakpoint("xxsmall"){max-width: 350px;}
                }
                .form-input {
                    border: 1px solid #b3b2b2;
                }
                .container,.form-field{
                    position: relative;
                }
                .form-input{
                    padding: 0 35px 0 10px; 
                    height: 40px;
                }
                button{
                    position: absolute;
                    right: 0;
                    top: 0;
                    bottom: 0;
                    width: 30px;
                    padding: 0;
                    @include displayflexaligncenter;
                }
                .quickSearchResults{
                    margin-top: 0;
                    @media (max-width: 990px){
                        max-width: 350px;
                        margin: 0 auto;
                        left: 0;
                    }
                    @media (max-width: 479px){
                        max-width: 290px;
                    }
                }
            }
        }
    }
}
.navPages
{	max-width:none; text-align:left;
}
.navPages-action {padding:8px 0; font-weight:400; text-transform:none; font-size:16px; line-height:28px; border-bottom:4px solid #fff; color:#000;
    @include breakpoint("medium"){margin:0 30px;}
}
.navPages-action:hover, .navPages-action.is-open
{	border-bottom:4px solid #000;
}
.navPages-action.is-open 
{	background:none;
}
.navPages-item:first-child .navPages-action
{	margin-left:0; 
}
.dropdown--quickSearch 
{	padding:0; background:#fff; 
}
.dropdown--quickSearch.is-open 
{	left:32px !important; top:0 !important; max-width:1220px;
}
.navUser-action--quickSearch {	width:34px; height:46px; padding:9px 0; position:relative; z-index:51;background: #ffffff;
    @include breakpoint("medium"){
    display: block;
    }
}
.dropdown--quickSearch .form 
{	max-width:none;
}
.dropdown--quickSearch .form-input 
{	height:45px; padding:0 0 0 4px; border-radius:0; font-size:18px; border:0; color:#000;
}
.navPages-container {
    @include breakpoint("medium"){padding-left:64px; float:left; width:100%; margin:-48px 0 0;}
}
#menu .navPage-subMenu-item .navPages-action {
    padding:0; line-height:27px; margin:0; border:0; font-family:'FuturaEFOP';
    @include breakpoint("medium"){
        font-size:18px; font-weight:700;
    }
}
#menu .subcat-expand {
    @include breakpoint("medium"){display: none;}
}
.navPage-subMenu {
    background:#fff;
    transition: all .3s ease;
    -webkit-transition: all .3s ease;
    -moz-transition: all .3s ease;
    transform: scaleY(1);
    -webkit-transform: scaleY(1);
    -moz-transform: scaleY(1);
    -o-transform: scaleY(1);
    -ms-transform: scaleY(1);
	-webkit-transform-origin: top;
    -moz-transform-origin: top;
    -ms-transform-origin: top;
	width:100%;
    float:left;
    left:0;
    right:0;
}

#menu .navPages-action.is-open + .navPage-subMenu {
    visibility:visible; opacity:1; top:48px;margin-bottom: 10px;
    @include breakpoint("medium"){
        margin-bottom: 0;
    }
    .navPage-subMenu-list {
        margin: 15px 0 0 15px;
        @include breakpoint("medium"){
            margin: auto;
        }
    }
}
#menu .navPage-childList-item .navPages-action{font-weight:400; font-family:'FuturaEFOP-Book'; font-size:16px; padding-bottom:4px; line-height:24px;}
.navPages-list{
 + .navPages-list{
    border-top: 0;
    margin-top: 0;
    padding-top: 0;
    &.navPages-list--user{
        font-size: 0;
    }
 }
}
.heroCarousel.slick-initialized,.heroCarousel-slide .heroCarousel-image-wrapper, .heroCarousel-slide .heroCarousel-image
{	max-height:none; margin:0 auto;
}
#banner_1 {	width:100%; float:left; margin:0px 0 0;overflow: hidden;}
