}
#banner_1 ul li a 
{   display:block;
}
#banner_1 ul li img 
{	width:100%;
}
#banner_1 ul li .cb_cont {text-align:center; padding:10px 5px 40px;
    @include breakpoint("small"){padding:10px 15px 40px;}
}
