.productView-thumbnail {@include breakpoint("small"){display:block; width:auto; float:none; padding:0;}}
.productView-thumbnail-link{border:0; height:80px; max-width:80px; padding:0;display: inline-block;
    @include breakpoint("small"){margin-bottom: 5px;}
    &:hover, &.is-active{border:1px solid #000;}
}
.productView-image {@include breakpoint("small"){width: calc(100% - 95px); float:right;}}
.productView-image + .productView-thumbnails {
    @include breakpoint("small"){
        width: 85px; margin: 40px 0; float: left;
    }
    .slick-list{margin: 0;}
    img{
        max-height: 80px;
        max-width: 80px;
        z-index: -1;
    }
    .slick-arrow{
        transform: rotate(90deg);
        -webkit-transform: rotate(90deg);
        -moz-transform: rotate(90deg);
        -ms-transform: rotate(90deg);
        -o-transform: rotate(90deg);
    }
    .slick-prev{
        top: -40px;
        left: 0;
        right: 0;
        margin-right: auto;
        margin-left: auto;
    }
    .slick-next{
        bottom: -50px;
        top: auto;
        left: 0;
        right: 0;
        margin-right: auto;
        margin-left: auto;
    }
    .slick-dots{
        bottom: 0;
        li{
            height: auto;
            width: auto;
            padding-top: 0;
            padding-bottom: 0;
            button{
                &:before{
                    left: 1px;
                    top: 1px;
                }
                &:hover:before {
                    background: #000000;
                }
            }
            &.slick-active{
                button{
                    border-color: #000000;
                    &:before{
                        color: #000000;
                        background: #000000;
                    }
                }
            }
        }
    }
}
