@media only screen and (min-width:991px) {   
    body{padding-top:121px;}
    .header, .header_fixed, .header-logo, .header_fixed .header-logo, .header_top, .header_fixed .header_top,
    .navUser .navUser-section--alt .navUser-action, .header_fixed .navUser .navUser-section--alt .navUser-action
    {   transition: all .2s linear; -webkit-transition: all .2s linear; -moz-transition: all .2s linear;
        transition-duration: 250ms;
    }
    .header
    {   position:fixed; height:121px; transition-duration: 250ms;
    }  
    .stickyActive
    {   height:64px; transition-duration: 250ms;
    }        
    .header-logo
    {   transform: scale(1); transform-origin: left center; 
    }
    .header_fixed .header-logo
    {   transform: scale(0.786);
    }
    .stickyActive .navPages-container
    {   transform: translateY(-34px); opacity:0; transition-property: transform, opacity; visibility:hidden;
    }
    .header_top
    {   transform: translateY(0px);   
    }
    .header_fixed .header_top
    {   transform:translateY(-7px);   
    }
    .header_fixed .navUser .navUser-section--alt .navUser-action
    {   font-size:0;
    }
    .header_fixed .navUser-action svg
    {   margin-top:-1px;
    }
    .header_fixed .navUser-item--account .navUser-action, .header_fixed .navUser-item--cart .navUser-action
    {   margin-left:35px;
    }
    .stickyActive .header-logo .navUser-action--quickSearch
    {   opacity:0; visibility:hidden;
    }
    .header-logo + .navUser-action--quickSearch
    {   opacity:0; visibility:hidden; position:absolute; left:290px; padding:4px 0; width:auto; height:auto;
    }
    .stickyActive .header-logo + .navUser-action--quickSearch
    {   opacity:1; visibility:visible;
    }
    .stickyActive .header_bottom .navUser-item-search
    {   opacity:0; transform:translateY(-34px); transition-timing-function:ease-out; transition-duration:200ms;
    }
    .stickyActive .header_bottom .navUser-item-search .navUser-action--quickSearch
    {   pointer-events:none;
    }
    .stickyActive .header_bottom 
    {   transform:translateY(-120px);
    }
}

